			for (S s : list) {
				System.out.println(s.id + " " + s.score);
			}
		}
	}

	int getScore(int[] as) {
		int[] fs = new int[10];
		int i = 0;
		for (int f = 0; f < 10; ) {
			int cur = 0;
			cur += as[i];
			if (cur == 10) { // strike
				cur += as[i + 1] + as[i + 2];
				i += 1;
			} else { 
				cur += as[i + 1];
				if (cur == 10) { // spear
					cur += as[i + 2];
				}
				i += 2;
			}
			fs[f++] = cur;
		}
		int sum = 0;
		for (int x : fs) sum += x;
		return sum;
	}

	static class S implements Comparable<S> {
		int id;
		int score;
		@Override
		public int compareTo(S o) {
			if (score != o.score) return -(score - o.score);
			return id - o.id;
		}
	}
}

