import java.util.Arrays;
import java.util.Comparator;
import java.util.Scanner;
import java.util.StringTokenizer;

public class Main {

	public static void main(String[] args) {
		Scanner scanner = new Scanner(System.in);
		while (true) {
			int n = Integer.parseInt(scanner.nextLine());
			if (n == 0) {
				break;
			}
			int[][] num = new int[n][2];
			for (int l = 0; l < n; l++) {
				StringTokenizer tokenizer = new StringTokenizer(
						scanner.nextLine());
				num[l][0] = Integer.parseInt(tokenizer.nextToken());
				int t = tokenizer.countTokens();
				int[] b = new int[t];
				int frame = 1;
				for (int i = 0; i < t; i++) {
					b[i] = Integer.parseInt(tokenizer.nextToken());
				}

				int j = 0;
				int sum = 0;
				while (j < b.length) {
					if (frame >= 10) {
						sum += b[j];
						j++;
					} else {
						sum += b[j] + b[j + 1];
						if (b[j] == 10) {
							sum += b[j + 2];
							j++;
						} else {
							if (b[j] + b[j + 1] == 10) {
								sum += b[j + 2];
							}
							j += 2;
						}
					}
					frame++;
				}
				num[l][1] = sum;
			}
			Arrays.sort(num, new Comparator<int[]>() {

				@Override
				public int compare(int[] a, int[] b) {
					if (a[1] == b[1]) {
						return Integer.compare(a[0], b[0]);
					}
					return Integer.compare(b[1], a[1]);
				}
			});
			for (int[] a : num) {
				System.out.println(a[0] + " " + a[1]);
			}
		}
	}
}

import java.util.PriorityQueue;
import java.util.Scanner;

//Bowling
public class Main{

	static class T implements Comparable<T>{
		int id;
		int s;
		public T(int id, int s) {
			this.id = id;
			this.s = s;
		}
		public int compareTo(T o) {
			return s==o.s?id-o.id:o.s-s;
		}
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		while(true){
			int n = sc.nextInt();
			if(n==0)break;
			PriorityQueue<T> q = new PriorityQueue<T>();
			while(n--!=0){
				int[][] p = new int[10][3];
				int x = sc.nextInt();
				boolean[] strike = new boolean[9];
				boolean[] spare = new boolean[9];
				int f = 0;
				while(f<10){
					int pin = sc.nextInt();
					p[f][0] = pin;
					if(pin==10){
						if(f<9){
							strike[f++] = true;
							continue;
						}
					}
					pin = sc.nextInt();
					p[f][1] = pin;
					if(f<9&&p[f][0]+p[f][1]==10){
						spare[f] = true;
					}
					else if(p[f][0]+p[f][1]>=10){
						p[f][2]=sc.nextInt();
					}
					f++;
				}
				int sum = 0;
				for(int i=0;i<9;i++){
					sum += p[i][0] + p[i][1];
					if(strike[i]){
						sum += p[i+1][0];
						int d = p[i+1][0];
						if(i+1<9 && d==10)sum+=p[i+2][0];
						else sum+=p[i+1][1];
					}
					else if(spare[i]){
						sum += p[i+1][0];
					}
				}
				sum += p[9][0]+p[9][1]+p[9][2];
				q.add(new T(x,sum));
			}
			while(!q.isEmpty()){
				T t = q.poll();
				System.out.println(t.id+" "+t.s);
			}
		}
	}
}

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.Scanner;

public class Main {

	public static class Result implements Comparable<Result>{
		int number, score;

		public Result(int number, int score) {
			super();
			this.number = number;
			this.score = score;
		}

		@Override
		public int compareTo(Result o) {
			if(this.score != o.score){
				return o.score - this.score;
			}else{
				return this.number - o.number;
			}
		}

		public String toString(){
			return this.number + " " + this.score;
		}
	}

	public static void main(String args[]) {
		Scanner sc = new Scanner(System.in);

		while (true) {

			final int m = sc.nextInt();

			if (m == 0) {
				break;
			}

			ArrayList<Result> result = new ArrayList<Result>();

			for (int tt = 0; tt < m; tt++) {
				boolean[] is_strike = new boolean[10];
				boolean[] is_spare = new boolean[10];
				int[] count_score = new int[21];
				int[] frame_score = new int[10];
				int[] sum_score = new int[10];

				final int number = sc.nextInt();

				for (int frame = 0; frame < 10; frame++) {
					if (frame != 9) {
						final int score_1 = sc.nextInt();
						count_score[frame * 2] = score_1;

						if (score_1 == 10) {
							is_strike[frame] = true;
							frame_score[frame] = score_1;
							continue;
						}

						final int score_2 = sc.nextInt();
						count_score[frame * 2 + 1] = score_2;

						if (score_1 + score_2 == 10) {
							is_spare[frame] = true;
						}

						frame_score[frame] = score_1 + score_2;
					} else {
						final int score_1 = sc.nextInt();
						count_score[frame * 2] = score_1;

						final int score_2 = sc.nextInt();
						count_score[frame * 2 + 1] = score_2;

						if (score_1 == 10 || score_1 + score_2 == 10) {
							final int score_3 = sc.nextInt();
							count_score[frame * 2 + 2] = score_3;
							frame_score[frame] = score_1 + score_2 + score_3;
						} else {
							frame_score[frame] = score_1 + score_2;
						}
					}
				}

				//System.out.println(Arrays.toString(count_score));

				for (int frame = 0; frame < 10; frame++) {
					sum_score[frame] = (frame == 0 ? 0 : sum_score[frame - 1]) + frame_score[frame];

					if (is_spare[frame] || is_strike[frame]) {
						sum_score[frame] += count_score[(frame + 1) * 2];

						if (is_strike[frame]) {
							if (is_strike[frame + 1]) {
								sum_score[frame] += count_score[(frame + 2) * 2];
							} else {
								sum_score[frame] += count_score[(frame + 1) * 2 + 1];
							}
						}
					}
				}

				//System.out.println(Arrays.toString(sum_score));

				result.add(new Result(number, sum_score[9]));
			}



			Collections.sort(result);

			for(Result res : result){
				System.out.println(res);
			}
		}

	}
}

