class score_comp implements Comparator<Bowl>{
	public int compare(Bowl b1, Bowl b2){
		int d = 0;
		if((d = b1.score - b2.score) != 0)
			return -d;
		else
			return b1.id - b2.id;
	}
}

class Bowl{
	int[] pins;
	int n;
	int id;
	int score;

	public Bowl(int id, int n){
		this.id = id;
		this.n = n;
		this.score = 0;
		pins = new int[n];
	}

	public void get_score(){
		int m = 0;
		this.score = 0;
		for(int i=1; i<=10; i++){
			if(i==10){
				for(int k=m; k<n; k++){
					score += pins[k];
				}
			}
			else if(pins[m] == 10){
				score += pins[m] + pins[m+1] + pins[m+2];
				m++;
			}
			else{
				if(pins[m]+pins[m+1] == 10){
					score += pins[m] + pins[m+1] + pins[m+2];
				}
				else{
					score += pins[m] + pins[m+1];
				}
				m+=2;
			}
		}
	}
}

