		int socre;

		public int compareTo(Player o) {
			return this.number - o.number;
		}
	}

	public static void main(String srgs[]) {
		Scanner sc = new Scanner(System.in);
		int n;
		Player player[];
		int score[];

		while (true) {
			n = sc.nextInt();
			sc.nextLine();
			if (n == 0) {
				break;
			}
			player = new Player[n];
			score = new int[n];
			for (int i = 0; i < n; i++) {
				String s[] = sc.nextLine().split(" ");
				player[i] = new Player();
				player[i].number = Integer.valueOf(s[0]);
				int index = 1;
				for (int j = 0; j < 10; j++) {
					if (j != 9) {
						if (Integer.valueOf(s[index]) == 10) {
							player[i].socre += Integer.valueOf(s[index]) + Integer.valueOf(s[index + 1]) + Integer.valueOf(s[index + 2]);
							index += 1;
						} else if (Integer.valueOf(s[index]) + Integer.valueOf(s[index + 1]) == 10) {
							player[i].socre += Integer.valueOf(s[index]) + Integer.valueOf(s[index + 1]) + Integer.valueOf(s[index + 2]);
							index += 2;
						} else {
							player[i].socre += Integer.valueOf(s[index]) + Integer.valueOf(s[index + 1]);
							index += 2;
						}
					} else {
						player[i].socre += Integer.valueOf(s[index]) + Integer.valueOf(s[index + 1]);
						if (index + 2 < s.length) {
							player[i].socre += Integer.valueOf(s[index + 2]);
						}
					}
				}
				score[i] = player[i].socre;
			}
			Arrays.sort(player);
			Arrays.sort(score);
			for (int i = 0; i < n; i++) {
				if (i != n - 1 && score[i] == score[i + 1]) {
					score[i] = 0;
				}
			}
			int index = 0;
			for (int i = 0; i < n; i++) {
				if (score[i] != 0) {
					score[index++] = score[i];
				}
			}
			for (int i = index - 1; 0 <= i; i--) {
				for (int j = 0; j < n; j++) {
					if (player[j].socre == score[i]) {
						System.out.println(player[j].number + " " + player[j].socre);
					}
				}
			}
		}
	}
}

