		while (true) {
			int m = sc.nextInt();
			if (m == 0)
				break;
			Integer[] nums = new Integer[m];
			final Map<Integer, Integer> p = new HashMap<Integer, Integer>();
			for (int i = 0; i < m; i++) {
				nums[i] = sc.nextInt();
				boolean st[] = new boolean[10];
