public class hello
{
    public static void Main()
    {
        while (true)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            var ps = new P[n];
            for (int i = 0; i < n; i++)
            {
                string[] line = Console.ReadLine().Trim().Split(' ');
                var a = Array.ConvertAll(line, int.Parse);
                ps[i] = new P { id = a[0], s = getScore(a) };
            }
            foreach (var x in ps.OrderByDescending(y => y.s).ThenBy(y => y.id))
                Console.WriteLine("{0} {1}", x.id, x.s);
        }
    }
    static int getScore(int[] a)
    {
        var p = 1; var s = 0; var f = 1;
        while (f < 10)
        {
            if (a[p] == 10) { s += 10 + a[p + 1] + a[p + 2]; p++; }
            else if (a[p] + a[p + 1] == 10) { s += 10 + a[p + 2]; p += 2; }
            else { s += a[p] + a[p + 1]; p += 2; }
            f++;
        }
        for (int i = p; i < a.Length; i++) s += a[i];
        return s;
    }
}

