            playerNumber = int.Parse(input[0]);
            scoreCard = new int[input.Length - 1];
            for (int i = 0; i < scoreCard.Length; i++) {
                scoreCard[i] = int.Parse(input[i+1]);
            }

            Flame[] result = new Flame[10];
            for (int i = 0; i < result.Length; i++) {
                result[i] = new Flame();
            }

            bool inFrame = false;
            int flameCount = 0;
            int s = 0;
            while (true) {

                if (flameCount == 9) {
                    switch (scoreCard.Length - s) {
                        case 3:
                            result[flameCount].setScore(scoreCard[s],scoreCard[s+1],scoreCard[s+2]);
                            break;
                        case 2:
                            result[flameCount].setScore(scoreCard[s],scoreCard[s+1],0);
                            break;
                    }
                    break;
                }
                if (scoreCard[s] == 10) {
                    if (s > 1) {
                        if (inFrame) {
                            result[flameCount].setScore(0, scoreCard[s], 0);
                            flameCount++;
                            s++;
                        } else {
                            result[flameCount].setScore(scoreCard[s], 0, 0);
                            flameCount++;
                            s++;
                        }
                    } else {
                        result[flameCount].setScore(scoreCard[s], 0, 0);
                        flameCount++;
                        s++;
                    }
                } else {
                    if (!inFrame) { 
                        s++;
                        inFrame = true;
                    } else {
                        result[flameCount].setScore(scoreCard[s - 1], scoreCard[s], 0);
                        flameCount++;
                        s++;
                        inFrame = false;
                    }
                }
            }


            for (int f = 0; f < result.Length; f++){
                if (f == 9) {
                    totalScore += result[f].getScore()[0] + result[f].getScore()[1]+result[f].getScore()[2];
                } else if (f == 8) {
                    if (result[f].getIsStrike()){
                        totalScore += 10 + result[f + 1].getScore()[0] + result[f + 1].getScore()[1];
                    } else if (result[f].getIsSpare()){
                        totalScore += 10 + result[f + 1].getScore()[0];
                    } else {
                        totalScore += result[f].getScore()[0] + result[f].getScore()[1];
                    }
                } else if (f < 8) {
                    if (result[f].getIsStrike()) {
                        if (result[f+1].getIsStrike()){
                            totalScore += 20 + result[f+2].getScore()[0];
                        } else {
                            totalScore += 10 + result[f + 1].getScore()[0] + result[f + 1].getScore()[1];
                        }
                    } else if(result[f].getIsSpare()) {
                        totalScore += 10 + result[f + 1].getScore()[0];
                    } else {
                        totalScore += result[f].getScore()[0] + result[f].getScore()[1];
                    }
                } 
            }
        }

