using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace ConsoleApplication1
{
    class Player : IComparable
    {
        internal int id, point;

        public Player(int id, int point)
        {
            this.id = id;
            this.point = point;
        }

        public override string ToString()
        {
            return id + " " + point;
        }

        public int CompareTo(object obj)
        {
            Player other = obj as Player;

            if (point == other.point)
            {
                return id - other.id;
            }
            return other.point - point;
        }
    }

    class Program
    {
        static void Main(string[] args)
        {
            StringBuilder sb = new StringBuilder();

            while (true)
            {
                int n = int.Parse(Console.ReadLine());

                if (n == 0) break;

                Player[] players = new Player[n];

                for (int i = 0; i < n; i++)
                {
                    int[] input = Array.ConvertAll(Console.ReadLine().Split(' '), int.Parse);

                    var query = input.Skip(1).ToArray();

                    int point = CalcPoint(query);

                    players[i] = new Player(input[0], point);
                }

                Array.Sort(players);

                foreach (var p in players)
                {
                    sb.AppendLine(p.ToString());
                }
            }
            Console.Write(sb);
        }

        static int CalcPoint(int[] arr)
        {
            int ret = 0, idx = 0;

            for (int game = 1; game < 10;)
            {
                if (arr[idx] == 10)
                {
                    ret += (arr[idx] + arr[idx + 1] + arr[idx + 2]);
                    idx++;
                    game++;
                }
                else
                {
                    int temp = arr[idx] + arr[idx + 1];

                    if (temp == 10)
                    {
                        ret += (temp + arr[idx + 2]);
                    }
                    else
                    {
                        ret += temp;
                    }

                    idx += 2;
                    game++;
                }
            }

            for (int i = idx; i < arr.Length; i++)
            {
                ret += arr[i];
            }
            return ret;
        }
    }
}

