public class Score
{
    public int Cal(int[] arr)
    {
        int arrcount = 1;
        int toume = 1;
        int total = 0;
        for (int y = 0; y < 10; y++)
        {
            if (y != 9) //９フレまで
            {
                if (toume == 1)　//一投目
                {
                    if (arr[arrcount] == 10)//ストライク
                    {
                        total =  total + arr[arrcount] + arr[arrcount + 1] + arr[arrcount + 2];
                        arrcount = arrcount + 1;
                    }
                    else
                    {
                        toume = 2;
                    }
                }
                if (toume == 2)　//二投目
                {
                    if (arr[arrcount] + arr[arrcount + 1] == 10)//スペア
                    {
                        total = total + 10 + arr[arrcount + 2];
                        arrcount = arrcount + 2;
                        toume = 1;
                    }
                    else
                    {
                        total = total + arr[arrcount] + arr[arrcount+1];
                        arrcount = arrcount + 2;
                        toume = 1;
                    }
                }
            }
#region 10フレ 
            else //１０フレ
            {
                if (toume == 1)　//一投目
                {
                    if (arr[arrcount] == 10)//ストライク
                    {
                        total =  total + arr[arrcount] + arr[arrcount + 1] + arr[arrcount + 2];
                    }
                    else
                    {
                        toume = 2;
                    }
                }

                if (toume == 2)　//二投目
                {
                    if (arr[arrcount] + arr[arrcount + 1] == 10)//スペア
                    {
                        total = total + 10 + arr[arrcount + 2];
                    }
                    else
                    {
                        total = total + arr[arrcount] + arr[arrcount+1];
                    }
                }
            }
#endregion            
        }
        return total;
    }
}

