					int ret = 0, ptr = 0;
					for(int j = 0; j < 9; j++) {
						if (a[ptr] == 10) { ret += 10 + a[ptr + 1] + a[ptr + 2]; ptr++; }
						else if (a[ptr] + a[ptr + 1] == 10) { ret += 10 + a[ptr + 2]; ptr += 2; }
						else { ret += a[ptr] + a[ptr + 1]; ptr += 2; }
					}
					for (int j = ptr; j < a.Length; j++) ret += a[ptr];
					d[i] = new Tuple<int, int>(-ret, id);
				}
				Array.Sort(d);
				for(int i = 0; i < n; i++) Console.WriteLine(d[i].Item2 + " " + -d[i].Item1);
			}
		}
	}
