using System;
using System.Collections.Generic;
using System.Linq;

namespace AOJ.Volume1
{
    public class Bowling
    {
        public static int Main()
        {
            while (true)
            {
                int counter = int.Parse(Console.ReadLine());
                if (counter == 0) { break; }

                var result = new List<KeyValuePair<int, int>>();
                for (int s = 0; s < counter; s++)
                {
                    var input = Console.ReadLine().Split(' ');

                    var pin = new int[21];
                    for (int i = 1; i < input.Length; i++) { pin[i - 1] = int.Parse(input[i]); }
                    int score = 0;
                    int pos = 0;
                    for (int frame = 1; frame <= 10; frame++)
                    {
                        // ストライク
                        if (pin[pos] == 10)
                        {
                            score += 10 + pin[pos + 1] + pin[pos + 2];

                            pos++;
                            continue;
                        }
                        // スペア
                        if (pin[pos] + pin[pos + 1] == 10)
                        {
                            score += pin[pos + 2];
                        }

                        // スペアの10点もここで加算
                        score += pin[pos] + pin[pos + 1];
                        pos = pos + 2;
                    }

                    result.Add(new KeyValuePair<int, int>(int.Parse(input[0]), score));
                }

                foreach (var kvp in result.OrderByDescending(x => x.Value).ThenBy(y => y.Key))
                {
                    Console.WriteLine(kvp.Key.ToString() + " " + kvp.Value.ToString());
                }
            }
            return 0;
        }
    }
}

