using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace ConsoleApplication1
{
    class Player : IComparable
    {
        internal int id, point;

        public Player(int id, int point)
        {
            this.id = id;
            this.point = point;
        }

        public override string ToString()
        {
            return id + " " + point;
        }

        public int CompareTo(object obj)
        {
            Player other = obj as Player;

            if (point == other.point)
            {
                return id - other.id;
            }
            return other.point - point;
        }
    }

    class Program
    {
        static void Main(string[] args)
        {
            StringBuilder sb = new StringBuilder();

            while (true)
            {
                int n = int.Parse(Console.ReadLine());

                if (n == 0) break;

                Player[] players = new Player[n];

                for (int i = 0; i < n; i++)
                {
                    int[] input = Array.ConvertAll(Console.ReadLine().Split(' '), int.Parse);

                    List<int> game = new List<int>();

                    for (int j = 1; j < input.Length; j++)
                    {
                        game.Add(input[j]);
                    }

                    int point = CalcPoint(game);

                    players[i] = new Player(input[0], point);
                }

                Array.Sort(players);

                foreach (var p in players)
                {
                    sb.AppendLine(p.ToString());
                }
            }
            Console.Write(sb);
        }

        static int CalcPoint(List<int> list)
        {
            int ret = 0, idx = 0;

            for (int game = 1; game < 10;)
            {
                if (list[idx] == 10)
                {
                    ret += (list[idx] + list[idx + 1] + list[idx + 2]);
                    idx++;
                    game++;
                }
                else
                {
                    int temp = list[idx] + list[idx + 1];

                    if (temp == 10)
                    {
                        ret += (temp + list[idx + 2]);
                    }
                    else
                    {
                        ret += temp;
                    }

                    idx += 2;
                    game++;
                }
            }

            for (int i = idx; i < list.Count; i++)
            {
                ret += list[i];
            }
            return ret;
        }
    }
}

