namespace AOJ
{
	class Program
	{
		static void Main(string[] args)
		{
			while(true)
			{
				int m = int.Parse(Console.ReadLine());
				if (m == 0) break;

				var kv = new List<KeyValuePair<int, int>>();

				while(m--!=0)
				{
					var s = Console.ReadLine().Split(' ').Select(i => int.Parse(i)).ToArray();
					var scores = s.Skip(1).ToArray();
					var num = s[0];
					int score = 0, index = 0;

					for (int i = 0; i < 10; i++)
					{
						int t = scores[index];
						if(t == 10)
						{
							score += t + scores[index + 1] + scores[index + 2];
							index++;
							continue;
						}

						t += scores[index + 1];
						if (t == 10)
						{
							score += t + scores[index + 2];
						}
						else
						{
							score += t;
						}
						index += 2;
					}
					kv.Add(new KeyValuePair<int,int>(num, score));
				}

				foreach(var r in kv.OrderByDescending(a => a.Value).ThenBy(a => a.Key))
				{
					Console.WriteLine("{0} {1}", r.Key, r.Value);
				}
			}
		}
	}
