public class State
{
    public int f { get; set; }
    public int th { get; set; }  // 1 or 2 or 3
    public int sc { get; set; }
    public bool dw { get; set; }   // th = 2 x
    public bool st { get; set; }
    public bool sp { get; set; }  // th = 2 x
    public int p { get; set; }
    public int tend { get; set; }
}
public class Score
{
    public int gid { get; set; }
    public int score { get; set; }
}

public class Hello
{
    public static void Main()
    {
        while (true)
        {
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            var scores = new List<Score>();
            for (int i = 0; i < n; i++)
            {
                string[] line = Console.ReadLine().Trim().Split(' ');
                var a = Array.ConvertAll(line, int.Parse);
                var s = new State { f = 1, th = 1, sc = 0, p = 1 };
                while (true)
                {
                    calcBowl(a, s);
                    if (s.tend == 1) break;
                }
                scores.Add(new Score { gid = a[0], score = s.sc });
            }
            foreach (var x in scores.OrderByDescending(y => y.score).ThenBy(y => y.gid))
                Console.WriteLine("{0} {1}", x.gid, x.score);
        }
    }
    public static void calcBowl(int[] a, State s)
    {
        var n = a[s.p];
        switch (s.th)
        {
            case 1:
                if (s.f == 10 && s.dw && n == 10) { s.sc += 30; s.th++; }
                else if (s.f == 10 && s.st && n == 10) { s.st = false; s.dw = true; s.th++; }
                else if (s.f == 10 && s.dw && n != 10) { s.sc += 20 + n; s.dw = false; s.st = true; s.th++; }
                else if (s.dw && n == 10 && s.f != 10) { s.sc += 30; s.f++; }
                else if (s.dw && n != 10) { s.sc += 20 + n; s.dw = false; s.st = true; s.th++; }
                else if (s.st && n == 10) { s.dw = true; s.st = false; s.f++; }
                else if (s.st && n != 10) { s.th++; }
                else if (s.sp && n == 10) { s.sc += 20; s.sp = false; s.st = true; s.f++; }
                else if (s.sp && n != 10) { s.sc += 10 + n; s.sp = false; s.th++; }
                else if (n == 10) { s.st = true; s.f++; }
                else { s.th++; }
                break;
            case 2:
                if (s.f == 10 && s.dw && n == 10) { s.sc += 50; s.th++; }
                else if (s.f == 10 && s.dw && n != 10) { s.sc += 30 + 2 * n; s.dw = false; s.th++; }
                else if (s.f == 10 && s.st && n + a[s.p - 1] == 10) { s.sc += 30; s.st = false; s.th++; }
                else if (s.f == 10 && s.st && n + a[s.p - 1] != 10) { s.sc += 10 + 2 * (n + a[s.p - 1]); s.tend = 1; return; }
                else if (s.f == 10 && n + a[s.p - 1] == 10) { s.sc += 10; s.th++; }
                else if (s.f == 10 && n + a[s.p - 1] != 10) { s.sc += n + a[s.p - 1]; s.tend = 1; return; }
                else if (s.st && n + a[s.p - 1] == 10) { s.sc += 20; s.st = false; s.sp = true; s.f++; s.th = 1; }
                else if (s.st && n + a[s.p - 1] != 10) { s.sc += 10 + 2 * (n + a[s.p - 1]); s.st = false; s.f++; s.th = 1; }
                else if (n + a[s.p - 1] == 10) { s.sp = true; s.f++; s.th = 1; }
                else { s.sc += n + a[s.p - 1]; s.f++; s.th = 1; }
                break;
            case 3:
                { s.sc += n; s.tend = 1; return; }
        }
        s.p++;
        return;
    }
}

