    class ResultBowling {
        int[] scoreCard;
        public int playerNumber;
        public int totalScore;

        public void calcScore(string[] input) {
            playerNumber = int.Parse(input[0]);
            scoreCard = new int[input.Length - 1];
            for (int i = 0; i < scoreCard.Length; i++) {
                scoreCard[i] = int.Parse(input[i+1]);
            }

            Flame[] result = new Flame[10];
            for (int i = 0; i < result.Length; i++) {
                result[i] = new Flame();
            }

            bool inFrame = false;
            int flameCount = 0;
            int s = 0;
            while (true) {

                if (flameCount == 9) {
                    switch (scoreCard.Length - s) {
                        case 3:
                            result[flameCount].setScore(scoreCard[s],scoreCard[s+1],scoreCard[s+2]);
                            break;
                        case 2:
                            result[flameCount].setScore(scoreCard[s],scoreCard[s+1],0);
                            break;
                    }
                    break;
                }
                if (scoreCard[s] == 10) {
                    if (s > 1) {
                        if (inFrame) {
                            result[flameCount].setScore(0, scoreCard[s], 0);
                            flameCount++;
                            s++;
                        } else {
                            result[flameCount].setScore(scoreCard[s], 0, 0);
                            flameCount++;
                            s++;
                        }
                    } else {
                        result[flameCount].setScore(scoreCard[s], 0, 0);
                        flameCount++;
                        s++;
                    }
                } else {
                    if (!inFrame) { 
                        s++;
                        inFrame = true;
                    } else {
                        result[flameCount].setScore(scoreCard[s - 1], scoreCard[s], 0);
                        flameCount++;
                        s++;
                        inFrame = false;
                    }
                }
            }


            for (int f = 0; f < result.Length; f++) {
                if (f == 9) {
                    if (result[f - 2].getIsStrike() && result[f - 1].getIsStrike()) totalScore += 20 + result[f].getScore()[0];
                    if (result[f-1].getIsStrike()) {
                        totalScore += 10 + result[f].getScore()[0] + result[f].getScore()[1];
                    } else if(result[f-1].getIsSpare()) {
                        totalScore += 10 + result[f].getScore()[0];
                    }
                    totalScore += result[f].getScore()[0] + result[f].getScore()[1] + result[f].getScore()[2];
                } else if(f >= 2 && f < 9) {
                    if (result[f - 2].getIsStrike() && result[f - 1].getIsStrike()) { totalScore += 20 + result[f].getScore()[0]; }
                    if (result[f].getIsStrike()) {
                        if (result[f - 1].getIsSpare()) {
                            totalScore += 10 + result[f].getScore()[0];
                        }
                        continue; 
                    }
                    if (result[f - 1].getIsStrike()) {
                        totalScore += 10 + result[f].getScore()[0] + result[f].getScore()[1];
                    }
                    if (result[f - 1].getIsSpare()) {
                        totalScore += 10 + result[f].getScore()[0];
                    }
                    if (result[f].getIsSpare()) continue;
                    totalScore += result[f].getScore()[0] + result[f].getScore()[1];
                } else {
                    if (f > 0) {
                        if (result[f-1].getIsSpare()) {
                            totalScore += 10 + result[f].getScore()[0];
                        }
                        if (result[f - 1].getIsStrike() && !result[f].getIsStrike()) {
                            totalScore += 10 + result[f].getScore()[0] + result[f].getScore()[1];
                        }
                    }
                    if (result[f].getIsStrike()) continue; 
                    if (result[f].getIsSpare()) continue;
                    totalScore += result[f].getScore()[0] + result[f].getScore()[1];
                }
            }
        }

        class Flame {
            int first, second,third;
            bool isStrike, isSpare;
            public void setScore(int a,int b,int c) {
                first = a;
                second = b;
                third = c;
                if (a == 10) {
                    isStrike = true;
                    return;
                }
                if (a < 10 && a + b == 10) {
                    isSpare = true;
                    return;
                }
            }

            public int[] getScore() {
                return new int[] { first, second, third };
            }

            public bool getIsStrike() {
                return isStrike;
            }
            public bool getIsSpare() {
                return isSpare;
            }
        }
    }

}

