int calc_point(int[] arr) {
  int i = 0;
  int[11] point;
  int strike = 0;
  int game = 1;
  while(game <= 10) {
    if (arr[i] == 10) {
      point[game] += 10 + arr[i + 1] + arr[i + 2];
      i += 1;
    }
    else {
      if (strike > 0) {
        int p = (arr[i] + arr[i + 1]);
        point[game - 1] += p;
        strike = 0;
      }

      if ((arr[i] + arr[i + 1]) == 10) {
        point[game] += 10 + arr[i + 2];
      }
      else {
        point[game] += (arr[i] + arr[i + 1]);
      }
      i += 2;
    }
    game += 1;
  }
  return point.reduce!("a + b");
}

