      int[] line = readln.chomp.split(" ").map!(to!(int)).array;
      people[line[1..$].calc_point] ~= line[0];
    }
    int[] keys = sort!("a > b")(people.keys).array;
    foreach(point; keys) {
      int[] ordered = sort!("a < b")(people[point]).array;
      foreach(id; ordered) {
        writeln(id, " ", point);
      }
    }    
  }
}

