	for (int i=1;i<=n;i++)
	{
		insert(d[i],i);
	}
	for (int i=1;i<n;i++)
	{
		long long op=d[i]-abs(n-2*siz[i]);
		long long oq=find(op);
		if (oq==0)
		{
			printf("-1\n");
			return 0;
		}
		else
		{
			fa[i]=oq;
			siz[oq]+=siz[i];
			con(i,oq);con(oq,i);
		}
	}
	dfs(n,0);
	if (ans!=d[n])
	{
		printf("-1\n");
		return 0;
	}
	else
	{
		for (int i=1;i<n;i++)
		{
			printf("%d %d\n",id[i],id[fa[i]]);
		}
	}
}

