		if (!used[to]) {
			dfs(to);
			cnt[s] += cnt[to];
			sum_dis[s] += sum_dis[to] + cnt[to];
		}
	}
}

void solve(){
	int n;
	scanf("%d", &n);
	REPN(i, 1, n) {
		scanf("%lld", &pp[i]._1);
		pp[i]._2 = i;
		sz[i] = 1;
		par[i] = i;
	}
	sort(pp + 1, pp + n + 1);
	int r = n;
	//int bads = 0;
	for(; r > 1; r--) {
		int a = pp[r]._2;
		ll val = pp[r]._1 - (n - 2 * sz[a]);
		int ind = lower_bound(pp + 1, pp + n + 1, mp(val, -1)) - pp;
		if (ind == n + 1 || pp[ind]._1 != val) {
			printf("-1\n");
			return;
		}
		int a1 = get_par(a);
		int b = pp[ind]._2;
		int b1 = get_par(b);
		if (a == b) {
			// Let's assume
			printf("-1\n");
			return;
		}
		par[a1] = b1;
		sz[b] += sz[a];
		g[a].pb(b);
		g[b].pb(a);
	}

	int s = pp[1]._2;
	dfs(s);
	if (sum_dis[s] != pp[1]._1) {
		// Let's assume
		printf("-1\n");
		return;
	}

	REPN(i, 1, n) {
		REP(j, 0, SZ(g[i])) {
			int to = g[i][j];
			if (to > i) {
				printf("%d %d\n", i, to);
			}
		}
	}
}   

