void dfs1(int x, int las) {
    sz[x] = 1;
    for (int y : G[x])
        if (y != las) {
            dfs1(y, x);
            sz[x] += sz[y];
            d[x] += d[y] + sz[y];
        }
}

bool dfs2(int x, int las) {
    if (mp[d[x]] != x)
        return false;
    for (int y : G[x])
        if (y != las) {
            d[y] = d[x] + N - 2 * sz[y];
            if (!dfs2(y, x))
                return false;
        }
    return true;
}

