const int MAX_N = 200010;
const int MAX_V = 100010;
const double eps = 1e-6;
const ll mod = 1000000007;
const int inf = 1 << 29;
const ll linf = 1LL << 60;
const double PI = 3.14159265358979323846;
///////////////////////////////////////////////////////////////////////////////////////////////////

int N;
vector<int> G[MAX_N];

pl D[MAX_N];
ll ts[MAX_N];
pi ans[MAX_N];

pl dist(int v, int p) {
	pl res(0, 1);
	rep(i, 0, sz(G[v])) {
		int n = G[v][i];
		if(n == p) continue;
		pl tmp = dist(n, v);
		res.fst += tmp.fst + tmp.sec;
		res.sec += tmp.sec;
	}
	return res;
}

void solve() {
	cin >> N;
	rep(i, 0, N) {
		cin >> D[i].fst;
		D[i].fst *= -1;
		D[i].sec = i;
	}
	rep(i, 0, N) ts[i] = 1;
	sort(D, D + N);
	rep(i, 0, N - 1) {
		int at = lower_bound(D, D + N, pl(D[i].fst + (N - 2 * ts[i]), -inf)) - D;
		if(at == N || D[at].fst != D[i].fst + (N - 2 * ts[i]) || at <= i) {
			cout << "-1\n"; return;
		}
		ts[at] += ts[i];
		ans[i] = pi(D[i].sec, D[at].sec);
		G[D[i].sec].pb(D[at].sec);
		G[D[at].sec].pb(D[i].sec);
	}
	if(dist(D[N - 1].sec, -1).fst != -D[N - 1].fst) {
		cout << "-1\n"; return;
	}
	rep(i, 0, N - 1) {
		cout << ans[i].fst + 1 << " " << ans[i].sec + 1 << "\n";
	}
}

