                    return 0;
                }
                now=-1;
                sa=-1;
              qq++;
            }
            else{
                pll p=v[y];
                if(p.first!=now+sa+2){
                    cout<<-1<<endl;
                    return 0;
                }
                ans.push_back(make_pair(par,p.second));
                par=p.second;
                now=p.first;
                sa+=2;
                used[p.second]=true;
            }
        }

        //cout<<now<<" ";
    }
    rep(i,0,ans.size()){
        if(ans[i].first>ans[i].second)swap(ans[i].first,ans[i].second);
    }
    sort(ans.begin(),ans.end());
    rep(i,0,ans.size())cout<<ans[i].first+1<<" "<<ans[i].second+1<<endl;
}

