using namespace std;

long long dfs(int nd, vector<long long> &vis, vector<vector<int>> &adjList)
{
  vis[nd] += 1;
  long long ans = 0;
  for(int cd : adjList[nd])
  {
    if(!vis[cd])
    {
      ans += dfs(cd, vis, adjList);
      vis[nd] += vis[cd];
    }
  }
  ans += vis[nd] - 1;
  return ans;
}

