typedef pair<int, int> P;

map<long long, int> ids;

long long D[100100];
int N;

void input(){
	scanf("%d", &N);
	for(int i = 0; i < N; ++i){
		scanf("%lld", D + i);
		ids[D[i]] = i;
	}
}

int sz[100100];

vector<P> ans;

vector<int> G[100100];
int dis[100100];

void dfs(int v){
	for(int i = 0; i < G[v].size(); ++i){
		int u = G[v][i];
		dis[u] = dis[v] + 1;
		dfs(u);
	}
}

bool solve(){
	for(int i = 0; i < N; ++i){
		sz[i] = 1;
	}
	sort(D, D + N);
	for(int i = N - 1; i >= 1; --i){
		int v = ids[D[i]];
		int s = sz[v];
		int dif = N - 2 * s;
		if(dif <= 0) return false;
		long long nd = D[i] - dif;
		if(ids.count(nd) == 0) return false;
		int p = ids[nd];
		G[p].push_back(v);
		sz[p] += sz[v];
		ans.push_back(P(v, p));
	}
	int root = ids[D[0]];
	dis[root] = 0;
	dfs(root);
	long long sum = 0;
	for(int i = 0; i < N; ++i){
		sum += dis[i];
	}
	if(sum != D[0]) return false;
	return true;
}

int main(){
	input();
	bool flg = solve();
	if(!flg){
		printf("-1\n");
	}else{
		for(int i = 0; i < ans.size(); ++i){
			printf("%d %d\n", ans[i].first + 1, ans[i].second + 1);
		}
	}
	return 0;
}

