        int pos = lower_bound(p + 1, p + n + 1, make_pair(tmp, 0)) - p;
        pii now = p[pos];
        if (pos >= i || now.first != tmp) return puts("-1"), 0;
        sz[fa[u] = now.second] += sz[u];
        printf("%d %d\n", fa[u], u);
    }

    return 0;
}

#include <bits/stdc++.h>
#define REP(i, n) for (int i = 0; (i) < (int)(n); ++ (i))
#define REP3(i, m, n) for (int i = (m); (i) < (int)(n); ++ (i))
#define REP_R(i, n) for (int i = int(n) - 1; (i) >= 0; -- (i))
#define REP3R(i, m, n) for (int i = int(n) - 1; (i) >= (int)(m); -- (i))
#define ALL(x) begin(x), end(x)
using ll = long long;
using namespace std;

vector<pair<int, int> > solve(int n, vector<ll> const & d) {
    map<ll, int> index;
    REP (i, n) {
        index[d[i]] = i;
    }

    int root = index.begin()->second;
    vector<pair<int, int> > edges;
    vector<vector<int> > children(n);
    vector<int> size(n, 1);
    for (auto it = index.rbegin(); ; ++ it) {
        ll d_i; int i; tie(d_i, i) = *it;
        if (i == root) break;
        int d_parent = d_i - (n - size[i]) + size[i];
        if (not index.count(d_parent)) {
            return vector<pair<int, int> >();
        }
        int parent = index[d_parent];
        edges.emplace_back(parent, i);
        children[parent].push_back(i);
        size[parent] += size[i];
    }

    vector<ll> d1(n);
    function<void (int)> fold = [&](int i) {
        for (int j : children[i]) {
            fold(j);
            d1[i] += d1[j] + size[j];
        }
    };
    fold(root);
    function<void (int, int)> reroot = [&](int i, int parent) {
        if (parent != -1) {
            d1[i] = d1[parent] - size[i] + (n - size[i]);
        }
        for (int j : children[i]) {
            reroot(j, i);
        }
    };
    reroot(root, -1);
    if (d != d1) {
        return vector<pair<int, int> >();
    }
    assert (false);

    return edges;
}

int main() {
    int n; cin >> n;
    vector<ll> d(n);
    REP (i, n) cin >> d[i];
    auto edges = solve(n, d);
    if (edges.empty()) {
        cout << -1 << endl;
    } else {
        for (auto edge : edges) {
            int i, j; tie(i, j) = edge;
            cout << i + 1 << ' ' << j + 1 << endl;
        }
    }
    return 0;
}

