int n,nn;
struct node{int p,f,l,r;long long d;};
node a[100010];
int oa[100010],ob[100010];

void qsort(int l,int r)
{
	int i,j;
	node m,t;
	i=l;j=r;
	m=a[(l+r)/2];
	while (i<=j)
	{
		while (a[i].d<m.d) i++;
		while (a[j].d>m.d) j--;
		if (i<=j)
		{
			t=a[i];a[i]=a[j];a[j]=t;
			i++;j--;
		}
	}
	if (l<j) qsort(l,j);
	if (i<r) qsort(i,r);
}

void qsort2(int l,int r)
{
	int i,j;
	int ma,mb,t;
	i=l;j=r;
	ma=oa[(l+r)/2];
	mb=ob[(l+r)/2];
	while (i<=j)
	{
		while (oa[i]<ma || (oa[i]==ma && ob[i]<mb)) i++;
		while (oa[j]>ma || (oa[j]==ma && ob[j]>mb)) j--;
		if (i<=j)
		{
			t=oa[i];oa[i]=oa[j];oa[j]=t;
			t=ob[i];ob[i]=ob[j];ob[j]=t;
			i++;j--;
		}
	}
	if (l<j) qsort2(l,j);
	if (i<r) qsort2(i,r);
}

