		a[i].f=-1;
		a[i].l=-1;
		a[i].r=-1;
	}
	qsort(1,n);
	bool ok=true;
	a[1].l=0;a[1].r=n-1;
	for (int i=2;i<=n;i++)
	{
		bool check=true;
		for (int j=i-1;j>=1;j--)
		{
			if (a[i].d-a[j].d>nn) 
			{
				check=false;break;
			}
			else
			{
				if ((a[i].d-a[j].d-nn)%2==0)
				{
					int l,r;
					l=(nn+(a[i].d-a[j].d))/2;
					r=(nn-(a[i].d-a[j].d))/2;
