					{
						a[i].f=a[j].p;
						a[i].l=nn-r;
						a[i].r=r;
						break;
					}
				}
			}
		}
		if (!check) {ok=false;break;}
	}
	if (ok)
	{
		for (int i=1;i<=n;i++)
		{
			if (a[i].f<a[i].p) {oa[i]=a[i].f;ob[i]=a[i].p;}
