int main() {
	ll n;	cin >> n;
	if (n == 2) {
		cout << -1 << endl;
		return 0;
	}
	vll d(n);	cin >> d;
	vll num(n, 1);
	map<ll, ll> mp;
	rep(i, n) {
		mp[d[i]] = i;
	}
	vector<pll> d2(n);
	rep(i, n) {
		d2[i] = mp(d[i], i);
		group[i] = i;
	}
	sort(all(d2));
	vector<pll> ret;
	for (ll i = n - 1; i > 0; i--) {
		ll id = d2[i].second;
		if (getGroup(id) != id)	continue;
		ll a = num[id];
		if (!mp.count(d[id] + 2 * a - n) || id == mp[d[id] + 2 * a - n]) {
			cout << -1 << endl;
			return 0;
		}
		ll to = mp[d[id] + 2 * a - n];
		mp.erase(d[id]);
		merge(id, to);
		num[to] += num[id];
		ret.push_back(mp(id + 1, to + 1));
	}
	rep(i, n - 1) {
		cout << ret[i].first << " " << ret[i].second << endl;
	}
	return 0;
}

