void no() {
	cout << -1 << endl;
	exit(0);
}

int main() {
	int N; cin >> N;
	vi D(N); rep(i, N) cin >> D[i];
	if (N <= 3) no();

	map<int, int> w2i;
	rep(i, N) w2i[D[i]] = i;

	vi nc(N);
	vi par(N, -1);
	maxPQ<pii> pq;
	rep(i, N) pq.push(mp(D[i], i));
	int root = -1;
	while (! pq.empty()) {
		pii t = pq.top();
		pq.pop();
		int w = t.first;
		int i = t.second;
		if (pq.empty()) {
			root = i;break;
		}
		int nw = w - N + 2 + nc[i];
		if (w2i.find(nw) == w2i.end()) no();
		par[i] = w2i[nw];
		nc[nw] += nc[i] + 1;
	}
	if (root == -1) no();
	vpii nci(N);
	rep(i, N) nci[i] = mp(nc[i], i);
	sort(nci.begin(), nci.end());
	vll dist(N);
	rep(k, N) {
		int n = nci[k].first;
		int i = nci[k].second;
		if (i == root) continue;
		dist[par[i]] += dist[i] * (1 + nc[i]) + 1;
	}
	if (dist[root] != D[root]) no();
	rep(i, N) if (i != root) cout << (i + 1) << " " << (1 + par[i]) << endl;
	return 0;
}

