ll dfs(Graph<int> &tree, int p, int v, int d) {
  ll sum = d;
  for (auto nv : tree[v]) {
    if (nv != p) {
      sum += dfs(tree, v, nv, d+1);
    }
  }
  return sum;
}

void solve() {
  int n; cin >> n;
  vector<pair<ll, int>> g(n);
  rep(i,n) {
    cin >> g[i].first;
    g[i].second = i;
  }
  sort(ALL(g));

  vi cnt(n);
  vector<pii> ans; ans.reserve(n-1);
  Graph<int> tree(n);

  rrep1(i,n) {
    ll d; int idx;
    tie(d, idx) = g[i];

    ll nd = d - (n-2) + 2*cnt[idx];
    auto itr = lower_bound(ALL(g), make_pair(nd, 0));

    if (itr->first != nd || nd >= d) {
      cout << -1 << endl;
      return;
    }

    int nidx = itr->second;
    cnt[nidx] += cnt[idx] + 1;
    ans.emplace_back(idx+1, nidx+1);
    tree[idx].emplace_back(nidx);
    tree[nidx].emplace_back(idx);
  }

  if (dfs(tree, -1, g[0].second, 0) != g[0].first) {
    cout << -1 << endl;
    return;
  }

  cout << ans << endl;
}


/* -------- <programs end> -------- */


signed main() {
  cin.tie(nullptr);
  ios::sync_with_stdio(false);
  cout << fixed << setprecision(12);
  solve();
  return 0;
}

