		mp[d[i]] = i + 1;
	}

	sort(di.begin(), di.end());
	reverse(di.begin(), di.end());

	for (int i = 0; i < n; i++)
		cnt[i] = 1;
	for (int i = 0; i < n - 1; i++){
		int v = di[i].second,
