#include <algorithm>
#include <iostream>
#include <set>
#include <vector>
#include <numeric>

using namespace std;

const int INF = 1e9;

struct DSU {
    vector<int> parent, set_size;
    set<int> roots;

    DSU () {}

    DSU(int n) {
        assign(n);
    }

    void assign(int n) {
        parent.resize(n);
        set_size.assign(n, 1);
        iota(parent.begin(), parent.end(), 0);
        roots.insert(parent.begin(), parent.end());
    }

    bool check(int a, int b) {
        return root(a) == root(b);
    }

    int root(int x) {
        while (x != parent[x]) {
            x = parent[x] = parent[parent[x]];
        }
        return x;
    }

    int size() { return roots.size(); }

    int size(int i) { return set_size[root(i)]; }

    bool join(int a, int b) {
        a = root(a), b = root(b);

        if (a == b) {
            return false;
        }

        if (set_size[a] < set_size[b]) {
            swap(a, b);
        }

        roots.erase(b);
        set_size[a] += set_size[b];
        parent[b] = a;
        return true;
    }
};


int main() {
    ios_base::sync_with_stdio(0);

    int n;
    cin >> n;
    set<pair<int, int>, greater<pair<int, int> > > s;
    for (int i = 1, d; i <= n; i++) {
        cin >> d;
        s.emplace(d, i);
    }
    vector<pair<int, int> > edges;
    DSU d(n);
    bool res = true;
    while (d.size() != 1 and res) {
        const int dist = s.begin()->first, i = s.begin()->second;
        const int next_dist = dist - (n - 2 * d.size(i - 1));
        s.erase(s.begin());
        auto it = s.upper_bound({next_dist, INF});
        if (it == s.end() or it->first != next_dist) {
            res = false;
        }
        else {
            edges.emplace_back(i, it->second);
            d.join(i - 1, it->second - 1);
        }
    }
    if (res) {
        for (auto p : edges) {
            cout << p.first << ' ' << p.second << '\n';
        }
    } else {
        cout << "-1\n";
    }
}

