int main()
{
    ios_base::sync_with_stdio(0);

    cin >> N;
    for (int i = 0; i < N; i++)
    {
        cin >> D[i];
        ord[D[i]] = i;
        ssize[D[i]] = 1;
        stot[D[i]] = 0;
    }

    for (auto it = ord.rbegin(); it != ord.rend(); it++)
    {
        ll cv = it -> first;
        int ind = it -> second;

        int csize = ssize[cv];
        if (csize == N) break;
