        stot[ntot] += (stot[cv] + ssize[cv]);
        edge[ord[ntot]].push_back(ind);
    }
    cout << "made it\n";

    int mloc = 0;
    for (int i = 0; i < N; i++)
        if (D[i] < D[mloc])
