bool solve() {
  ll n; cin >> n;
  vector<ll> D(n); cin >> D;
  if (n == 2) {
    if (D[0] != 1 || D[1] != 1) {
      return false;
    }
    cout << 1 << " " << 2 << endl;
    return true;
  }
  map<ll, set<ll>> m;
  auto erase = [&](ll v) {
    m[D[v]].erase(v);
  };
  rep(i, n) m[D[i]].insert(i);
  vector<bool> isLeaf(n, false);
  rep(i, n) {
    if (m[D[i] - (n-2)].size() > 0) {
      isLeaf[i] = true;
    }
  }
  priority_queue<P, vector<P>, greater<P>> Q;
  vector<ll> cnt(n, -1), sz(n, 1);
  rep(i, n) if (isLeaf[i]) {
    if (m[D[i] - (n-2)].size() != 1) return false;
    cnt[i] = 0;
    sz[i] = 1;
    Q.push(P(D[i], i));
  }
  ll root = -1;
  vector<P> edges;
  vector<vector<ll>> G(n);
  vector<bool> used(n, false);
  while ( !Q.empty() ) {
    P p = Q.top(); Q.pop();
    ll v = p.second;
    if (used[v]) continue;
    used[v] = true;
    // ll pd = D[v] + sz[v] - (n - sz[v]);
    ll pd = D[v] - n + 2 * sz[v];
    assert(m[pd].size() <= 1);
    if (m[pd].size() == 0) {
      used[v] = false;
      // if (root >= 0) return false;
      // root = v;
      continue;
    }
    ll par = *m[pd].begin();
    sz[par] += sz[v];
    Q.push(P(D[par], par));
    edges.eb(v, par);
    erase(v);
  }
  // if (root < 0) return false;
  if (edges.size() != n-1) return false;
  // assert(edges.size() == n-1);
  each(p, edges) {
    cout << p.first+1 << " " << p.second+1 << endl;
  }
  return true;
}
int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  if (!solve()) {
    cout << -1 << endl;
  }
}

