	cin >> N;
	if (N == 2) {
		cout << -1 << endl;
		return 0;
	}
	vector<long long int>v(N);
	map<long long int, int>m;
	map<long long int, int>used;
	L = LLONG_MAX;
	for (int i = 0; i < N; i++) {
		cin >> v[i];
		m[v[i]] = i + 1;
		L = min(L, v[i]);
	}
	vector<pair<int, int>>ans;
	sort(v.begin(), v.end());
	reverse(v.begin(), v.end());
	for (auto i : v) {
		long long int dif = N - 2;
		long long int box = i;
		while (box > L) {
			if (used[box])break;
			if (m.find(box - dif) == m.end()) {
				cout << "-1\n";
				return 0;
			}
			ans.push_back({ m[box],m[box - dif] });
			//cout << m[box] << " " << m[box - dif] << endl;
			used[box]++;
			box -= dif;
			dif -= 2;
		}
	}
	vector<vector<int>>edge(N + 1);
	for (auto i : ans) {
		edge[i.first].push_back(i.second);
		edge[i.second].push_back(i.first);
	}
	vector<int>dis(N + 1, MOD);
	dis[m[L]] = 0;
	queue<int>Q;
	Q.push(m[L]);
	while (!Q.empty()) {
		int cn = Q.front(); 
		Q.pop();
		for (auto i : edge[cn]) {
			if (dis[i] > dis[cn] + 1) {
				dis[i] = dis[cn] + 1;
				Q.push(i);
			}
		}
	}
	long long int sum = 0;
	for (int i = 1; i <= N; i++) {
		sum += dis[i];
	}
	if (sum != L) {
		cout << -1 << endl;
		return 0;
	}
	for (auto i : ans) {
		cout << i.first << " " << i.second << endl;
	}
	return 0;
}

