#include <bits/stdc++.h>
using namespace std;

typedef pair <int, int> pii;

const int MAX_N = 1e5 + 5;
int n, root;
long long Arr[MAX_N];
int h[MAX_N];
int Siz[MAX_N];
int Mark[MAX_N];
int par[MAX_N];
unordered_map <long long, int> Map;
vector <pii> edge;
int dp_d[MAX_N];
int dp_u[MAX_N];
vector <int> child[MAX_N];

bool cmp(int a, int b) {
	return Arr[b] < Arr[a];
}

void dfs1(int v) {
	for (int i = 0; i < child[v].size(); i++) {
		dfs1(child[v][i]);
		dp_d[v] += dp_d[child[v][i]] + Siz[child[v][i]];
	}
}

void dfs2(int v, int val) {
	dp_u[v] = val;
	for (int i = 0; i < child[v].size(); i++)
		dfs2(child[v][i], dp_d[v] + dp_u[v] - dp_d[child[v][i]] - Siz[child[v][i]] + (n - Siz[child[v][i]]));
}

int main() {
	ios::sync_with_stdio(false), cout.tie(0), cin.tie(0);
	cin >> n;
	for (int i = 0; i < n; i++) {
		cin >> Arr[i];
		Siz[i] = 1;
		h[i] = i;
		Map[Arr[i]] = i;
	}

	sort(h, h + n, cmp);

	for (int i = 0; i + 1 < n; i++) {
		int v = h[i];
		long long tmp = Arr[v] + Siz[v] - (n - Siz[v]);

		if (tmp < 0 || (Map[tmp] == 0 && Arr[0] != tmp)) {
			cout << -1 << "\n";
			return 0;
		}

		int u = Map[tmp];
		if (Mark[u]) {
			cout << -1 << "\n";
			return 0;
		}

		Siz[u] += Siz[v];
		par[v] = u;
		edge.push_back({v, u});
		child[u].push_back(v);
	}
	root = h[n - 1];
	dfs1(root);
	dfs2(root, 0);

	for (int i = 0; i < n; i++)
		if (dp_d[i] + dp_u[i] != Arr[i]) {
			cout << i << " " << dp_d[i] + dp_u[i] << "\n";
			cout << -1 << "\n";
			return 0;
		}

	for (int i = 0; i + 1 < n; i++)
		cout << edge[i].first + 1 << " " << edge[i].second + 1 << "\n";
	return 0;
}

