    map<ll, int> Dtov;
    rep(i, 0, N) cin >> D[i];
    rep(i, 0, N) Dtov[D[i]] = i + 1;

    sort(D.rbegin(), D.rend());
    vector<vector<int> > G(N + 1, vector<int>());
    vector<bool> used(D.size(), false);
