            ll tmp = D[i];
            ll dec = N - 2;
            while (tmp > D.back() && dec > 0) {
                if (find(all(D), tmp - dec) == D.end()) {
                    cout << -1 << endl;
                    return 0;
                } else {
                    ans.emplace_back(Dtov[tmp], Dtov[tmp - dec]);
