#define llint long long

using namespace std;
typedef pair<llint, llint> P;

llint N;
llint d[100005];
P D[100005];
vector<llint> G[100005];
map<llint, vector<P> > mp;

int main(void)
{
	cin >> N;
	for(int i = 1; i <= N; i++) cin >> d[i];
	for(llint i = 1; i <= N; i++) D[i] = make_pair(d[i], i);
	sort(D+1, D+N+1);
	reverse(D+1, D+N+1);

	for(int i = 1; i <= N; i++){
		if(mp.count(D[i].first) == 0){
			llint p = D[i].first+N-2;
			if(p > D[i].first) continue;
			mp[p].push_back(make_pair(D[i].second, 1));
		}
		else{
			llint x = 0;
			for(int j = 0; j < mp[D[i].first].size(); j++){
				G[D[i].second].push_back(mp[D[i].first][j].first);
				G[mp[D[i].first][j].first].push_back(D[i].second);
				x += mp[D[i].first][j].second;
			}
			x++;
			llint p = D[i].first+N-2*x;
			if(p > D[i].first) continue;
			mp[p].push_back(make_pair(D[i].second, x));
		}
	}
	vector<P> ans;
	for(int i = 1; i <= N; i++){
		for(int j = 0; j < G[i].size(); j++){
			if(G[i][j] > i) ans.push_back(make_pair(i, G[i][j]));
		}
	}
	if(ans.size() < N-1) cout << "-1" << endl;
	else{
		for(int i = 0; i < ans.size(); i++) cout << ans[i].first << " " << ans[i].second << endl;
	}

	return 0;
}

