const int maxn = 1000010;
map<ll,int> mp;
int sz[maxn];

struct Node {
	ll d;
	int id;
	void read(int i) {
		id = i;
		scanf("%I64d",&d);
	}
	bool operator < (const Node& b) const {
		return d > b.d;
	}
}ns[maxn];

int fa[maxn],vis[maxn];

int main() {
	int n;
	scanf("%d",&n);
	rep_(i,1,n) {
		ns[i].read(i);
		mp[ns[i].d] = i;
		sz[i] = 1;
	}
	sort(ns+1,ns+1+n);
	int ok = 1;
	rep_(i,1,n-1) {
		int id = ns[i].id;
		vis[id] = 1;
		ll val = ns[i].d+sz[id]-(n-sz[id]);
		if(mp[val] == 0 || vis[mp[val]]) {
			ok = 0;
			break;
		}
		else {
			fa[id] = mp[val];
			sz[mp[val]] += sz[id];
		}
	}
	if(ok == 0) printf("-1\n");
	else rep_(i,1,n) if(fa[i] != 0) printf("%d %d\n",i,fa[i]);
    return 0;
}

