	while (m.size() > 1) {
		auto  box = *m.rbegin();
		long long int c = box.first;
		int cn = box.second;
	//	cout << c << " " << cn << endl;
		long long int a, b = 0;
		for (long long int i = c / (N - 1) + !!(c % (N - 1)); i <= sqrt(c); i++) {
			if (c%i)continue;
			a = i;
			b = c / i;
			long long int st = (b  - (a - 1));
			long long int add = st;
		//	cout << a << " " << b << " " << st << " " << add << endl;
			bool flag = true;
			while (st < c) {
				if (m.find(st) == m.end()) {
