ll dfs (int u, int p) {
	ll s = dep[u];
	int sz = 1;
	for (auto v : edges[u]) {
		if (v == p) continue;
		dep[v] = dep[u] + 1;
		s += dfs(v, u);
		sz += sub[v];
	}
	sub[u] = sz;
	return s;
