using namespace std;
struct condition{
	int32_t vertex;
	int64_t total;
};
struct current{
	int32_t parent;
	int32_t vertex_num;
};
struct edge{
	int32_t v1;
	int32_t v2;
};
bool operator< (const condition& a,const condition& b){
	return a.total>b.total;
}
int main() {

	int32_t n;
	cin>>n;
	std::vector<condition> cd;
	vector<edge> ans;
	ans.reserve(n);
	cd.reserve(n);
	for(int i=0;i<n;i++){
		int64_t temp;
		cin>>temp;
		cd.push_back(condition{i+1,temp});
	}
	bool ans_possible=true;
	sort(cd.begin(),cd.end());
	map<int64_t,vector<current> > flow;
	for(auto i=cd.begin();i!=cd.end();i++){
		int32_t sum_vertex=0;

		if(flow.count(i->total)){
			vector<current>& t=flow.at(i->total);
			int64_t total_cost=0;
			for(auto k=t.begin();k!=t.end();k++){
				ans.push_back(edge{k->parent,i->vertex});
				sum_vertex+=(k->vertex_num);
				total_cost+=((k->vertex_num)*(k->vertex_num+1))/2;
			}
			ans_possible=(total_cost==i->total);

			flow.erase(i->total);
		}else{
			ans_possible=false;
		}

		sum_vertex++;

		int32_t parent_value=i->total-(n-2*sum_vertex);
		if(flow.count(parent_value)){
			flow.at(parent_value).push_back(current{i->vertex,sum_vertex});
		}else{
			vector<current> temp2;
			temp2.push_back(current{i->vertex,sum_vertex});
			flow.emplace(parent_value,temp2);
		}

	}
	if(flow.size()==1&&ans_possible){
		for(auto i=ans.begin();i!=ans.end();i++){
			cout<<i->v1<<" "<<i->v2<<endl;
		}
	}else{
		cout<<-1<<endl;
	}





	return 0;

}

