	}
	return hs[i];
}

void qsort(int l,int r)
{
	int i=l;int j=r;
	int m=d[(i+j)/2];
	while (i<=j)
	{
		while (d[i]>m) i++;
		while (d[j]<m) j--;
		if (i<=j)
		{
			swap(d[i],d[j]);
			swap(id[i],id[j]);
			i++;j--;
		}
	}
	if (l<j) qsort(l,j);
	if (r>i) qsort(i,r); 
}

