typedef long long LL; 
const int MAX_N = 1e5 + 5; 
struct Graph { int to, next; } e[MAX_N << 1]; 
int fir[MAX_N], e_cnt; 
void clearGraph() { memset(fir, -1, sizeof(fir)); e_cnt = 0; } 
void Add_Edge(int u, int v) { e[e_cnt] = (Graph){v, fir[u]}, fir[u] = e_cnt++; } 
int N, sz[MAX_N];
LL D[MAX_N]; 
struct node { LL D; int id; } a[MAX_N]; 
bool operator < (const node &l, const node &r) { return l.D < r.D; } 
int dis[MAX_N]; 
void dfs(int x, int fa) { 
	for (int i = fir[x]; ~i; i = e[i].next) {
		int v = e[i].to; if (v == fa) continue; 
		dis[v] = dis[x] + 1, dfs(v, x); 
	} 
} 
int main () { 
	clearGraph(); 
	scanf("%d", &N); 
	for (int i = 1; i <= N; i++) scanf("%lld", D + i), sz[i] = 1, a[i] = (node){D[i], i}; 
	sort(&a[1], &a[N + 1]); 
	for (int i = N; i > 1; i--) { 
		int x = a[i].id, u = 0; 
		LL ds = 2 * sz[x] - N + a[i].D; 
		u = lower_bound(&a[1], &a[N + 1], (node){ds, 0})->id; 
		if (D[u] != ds) return puts("-1") & 0; 
		sz[u] += sz[x]; 
		Add_Edge(u, x), Add_Edge(x, u); 
	} 
	dfs(1, 0);
	LL ds = 0; 
	for (int i = 1; i <= N; i++) ds += dis[i]; 
	if (ds != D[1]) return puts("-1") & 0; 
	for (int i = 0; i < e_cnt; i += 2) printf("%d %d\n", e[i].to, e[i ^ 1].to); 
    return 0; 
}

