        int t;

        if (v[x][i] == y) continue;

        t = dfs(v[x][i], x);
        c[x] += t;
        p[x] += p[v[x][i]] + t;
    }

    return c[x];
}

void dfs2(int x, int y, long long z) {
    int i;

    p[x] += z;

