typedef long long ll;

const int N = 100005;
int n, id[N], now[N], tot;
ll d[N];

struct cmp1 {
	bool operator()(int x, int y) {
		return d[x] < d[y] || (d[x] == d[y] && x < y);
	}
};
struct cmp2 {
	bool operator()(int x, int y) {
		return d[x] - 2 * now[x] < d[y] - 2 * now[y] || (d[x] - 2 * now[x] == d[y] - 2 * now[y] && x < y);
	}
};
set<int, cmp1> sl[2];
set<int, cmp2> sr[2];

int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; ++i)
		scanf("%lld", d + i), id[i] = i;
	vector<pair<int, int>> res;
	sort(id + 1, id + 1 + n, [](int x, int y) { return d[x] < d[y]; });
	now[id[1]] = n - 1;
	sr[d[id[1]] & 1].insert(id[1]);
	for (int i = 2; i <= n; ++i) {
		for (int t = 0; t < 2; ++t)
			while (!sr[t].empty()) {
				int x = *sr[t].begin();
				if (d[x] - 2 * now[x] > d[id[i]] - n)
					break;
				sl[t].insert(x), sr[t].erase(x);
			}
		if (sl[d[id[i]] + n & 1].empty())
			return puts("-1"), 0;
		int fa = *--sl[d[id[i]] + n & 1].end();
		if (d[fa] <= d[id[i]] - n)
			return puts("-1"), 0;
		sl[d[fa] & 1].erase(fa);
		res.emplace_back(fa, id[i]);
		now[id[i]] = (n - d[id[i]] + d[fa]) / 2;
		now[fa] -= now[id[i]]--;
		if (now[fa])
			sl[d[fa] & 1].insert(fa);
		if (now[id[i]])
			sr[d[id[i]] & 1].insert(id[i]);
	}
	for (const auto &p : res)
		printf("%d %d\n", p.first, p.second);
	return 0;
}

