using P = pair<int64_t, int64_t>;

int main() {
  size_t N;
  cin >> N;
  map<int64_t, P> mp;
  for (size_t i = 1; i <= N; i++) {
    int64_t x;
    cin >> x;
    mp[x] = make_pair(i, 1);  // 
  }
  if (N == 2) {
    cout << -1 << endl;
    return 0;
  }
  bool flag = true;  // 
  vector<P> graph;
  auto enditr = mp.rend();
  enditr--;
  for (auto itr = mp.rbegin(); itr != enditr; ++itr) {
    auto key = itr->first;
    auto value = itr->second;
    auto vertex = value.first;
    auto size = value.second;
    auto child_key = key - N + 2 * size;
    auto tmpitr = mp.find(child_key);
    if (tmpitr != mp.end()) {
      auto tmp = tmpitr->second;
      mp.at(child_key) = make_pair(tmp.first, tmp.second + size);
      graph.push_back(make_pair(vertex, tmp.first));
    } else {
      flag = false;
      break;
    }
  }
  if (flag) {
    for (const auto& e : graph) {
      cout << e.first << ' ' << e.second << endl;
    }
  } else {
    cout << -1 << endl;
  }
  return 0;
}

